
public class ForTest1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sum = 0;
		for(int i = 1; i <= 10; i ++) {
			sum += i;
			System.out.println(i+"까지의 합" + sum);
		}
		System.out.println(sum);
//		System.out.println(i-1 + "까지의 합:" + sum);// 에러

	}

	
	
	

}
